mapper.select:
SELECT id
FROM users
WHERE id = #{id}

SELECT user_seq, id, password, name, address, email, number, admin
FROM users
WHERE id = #{id}

SELECT id, name, address, email, number
FROM users
WHERE id = #{id}

mapper.update:
UPDATE users
SET id = #{id}, password = #{password}, number = #{number}, email = #{email}, 
	address = #{address}
WHERE user_seq = #{user_seq}
